import useCountry from '../../hooks/useCountry';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getCountryDetail, clearDetail, getCountries, getActivities } from '../../redux/actions/actions';
import { Link } from 'react-router-dom';
import './activities.styles.css';

function Activities() {
    const dispatch = useDispatch()

    const allActivities = useSelector((state) => state.allActivities);
    // if(!allCountries) = 

    const country = useCountry(state => state.countryDetail);



    const { name, flag, continent, capital, subregion, area, population } = country;
    useEffect(() => {
        if (!allActivities) {
            dispatch(getActivities())
        }
        // dispatch(getCountryDetail(id))

        return dispatch(clearDetail())
    }, [dispatch])

    return (
        // <>
        <div>
            <div>
                <Link to={'/home'}>
                    <button className='detailButton'>Menú Principal</button>
                </Link>
            </div>
        </div>

        // </>
    );
}

export default Activities;
