import useCountry from '../../hooks/useCountry';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getCountryDetail, clearDetail, getCountries, getActivities } from '../../redux/actions/actions';
import { Link } from 'react-router-dom';
import './activities.styles.css';

function Activities() {
    const dispatch = useDispatch()

    const allActivities = useSelector((state) => state.allActivities);
    // if(!allCountries) = 

    const country = useCountry(state => state.countryDetail);

    // function changeOrder(){
    //     allActivities = allActivities.sort((a, b) => a.name.localeCompare(b.name));
    // }

    useEffect(() => {
        if (!allActivities) {
            dispatch(getActivities())
        }
    }, [dispatch])

    return (
        <>
        <div>
            <div>
                <Link to={'/home'}>
                    <button className='detailButton'>Menú Principal</button>
                </Link>
            </div>
            <h1>Listado de actividades</h1>
            <button onClick={changeOrder}></button>
            <div className="activitiesList">
                {allActivities.map((activity) => (
                    <>
                    <h2 className='activityName'>{activity.name}</h2>
                    <p>Dificultad: {activity.difficulty}</p>
                    <p>Duración: {activity.duration}</p>
                    <p>Estación del año: {activity.season}</p>
                    <p>Países: {activity.country.join(", ")}</p>
                    </>
                ))}
            </div>
        </div>

         </>
    );
}

export default Activities;
